<script type='text/javascript'>
var draw_challenge_title = 'Draw Your Favorite Animal'
var canvas, ctx, oldMouseX, oldMouseY, mouseX, mouseY, mouseDown = 0;
var dotSize = 10, dotColor = '#FFF';
init();function init() {
    canvas = document.getElementById('sketchpad');
    ctx = canvas.getContext('2d');
    canvas.addEventListener('mousedown', sketchpad_mouseDown, false);
    canvas.addEventListener('mousemove', sketchpad_mouseMove, false);
    window.addEventListener('mouseup', sketchpad_mouseUp, false);
    clearCanvas();
}
function draw() {
	if(mouseX < 0 || mouseY < 0 || mouseX >= canvas.width || mouseY > canvas.height) {
		sketchpad_mouseUp();
	}
    ctx.strokeStyle = dotColor;
    ctx.lineWidth = dotSize;
    ctx.lineCap = 'round';
    ctx.beginPath();
    ctx.moveTo(oldMouseX, oldMouseY);
    ctx.lineTo(mouseX, mouseY);
    ctx.stroke();
	oldMouseX = mouseX;
	oldMouseY = mouseY;
}
function clearCanvas() {
  	ctx.fillStyle = '#000';
  	ctx.fillRect(0, 0, canvas.width, canvas.height);
}
function toggleBrush() {
	if(dotColor == '#000') {
		dotColor = '#FFF';
		document.getElementById('toggleBrush').innerText = 'Eraser';
	} else {
		dotColor = '#000';
		document.getElementById('toggleBrush').innerText = 'Brush';
	}
}
function changeBrushSize() {
	if(dotSize <= 5) {
		dotSize = 10;
		document.getElementById('changeBrushSize').innerText = 'Normal';
	} else if(dotSize <= 10) {
		dotSize = 25;
		document.getElementById('changeBrushSize').innerText = 'Large';
	} else if(dotSize <= 25) {
		dotSize = 5;
		document.getElementById('changeBrushSize').innerText = 'Small';
	}
}
function sketchpad_mouseDown() {
	oldMouseX = mouseX;
	oldMouseY = mouseY;
    mouseDown = 1;
    draw();
}
function sketchpad_mouseUp() {
    mouseDown = 0;
    try {
    	var title = document.getElementsByClassName('challenge-name')[0].innerText;
    	if(title == draw_challenge_title) {
			challenge = document.getElementById('challenge-input')
			challenge.value = canvas.toDataURL();
		}
    } catch(e) {}
}
function sketchpad_mouseMove(e) {
    getMousePos(e);
    if (mouseDown == 1) {
        draw();
    }
}
function getMousePos(e) {
    if (!e) e = event;
    if (e.offsetX) {
        mouseX = e.offsetX;
        mouseY = e.offsetY;
    } else if (e.layerX) {
        mouseX = e.layerX;
        mouseY = e.layerY;
    }
    mouseX /= 2;
    mouseY /= 2; // Double the canvas size
}
</script>

